import React from "react";
import { withRouter } from "react-router-dom";

class Business extends React.Component {
    constructor(props) {
        super(props)

      this.state = { 
        businesses: {} ,
        editBusiness: {},
        newBusiness: {}
      };

      this.chooseEdit = this.chooseEdit.bind(this);
      this.titlesArray = this.titlesArray.bind(this);
      this.createEditForm = this.createEditForm.bind(this);
    }

    componentDidMount() {
      this.props.fetchAll()
    }

    componentDidUpdate() {
      debugger
      if (this.props.businesses !== this.state.businesses) {
        this.setState({ businesses: this.props.businesses });
      }
    }

    chooseEdit(e) {
      e.preventDefault();
      debugger
      let newState = Object.assign({}, this.state.businesses[e.target.id], { id: [e.target.id] })
      this.setState({ editBusiness: newState})
    }

    titlesArray() {
      const { businesses } = this.state;
      let ids = Object.keys(businesses);
      let array = [];
      if (ids.length > 0) {
        for (let i = 0; i < ids.length; i++) {
          const id = ids[i];
          const business = businesses[id];
          array.push(
            <li key={id} id={id} onClick={this.chooseEdit}>
              Edit {business.displayName.hebrew}
            </li>
          )

        }
      }
      return array;
    }

  createEditForm() {
    debugger
    if (this.state.editBusiness.displayName) {
      return(
        <form>
          <input
            type="text"
            id="displayNameHebrew"
            value={this.state.editBusiness.displayName.hebrew}
            onChange={(e) => this.setState({
              editBusiness: {
                displayName: {
                  hebrew: e.currentTarget.value
                }
              }
            })}
            placeholder="displayName Hebrew"
          />
          <input
            type="text"
            id="displayNameEnglish"
            value={this.state.editBusiness.displayName.english}
            onChange={(e) => this.setState({
              editBusiness: {
                displayName: {
                  english: e.currentTarget.value
                }
              }
            })}
            placeholder="displayName Hebrew"
          />
          <input
            type="text"
            id="displayNameHebrew"
            value={this.state.editBusiness.displayName.hebrew}
            onChange={(e) => this.setState({
              editBusiness: {
                displayName: {
                  hebrew: e.currentTarget.value
                }
              }
            })}
            placeholder="displayName English"
          />
          <input
            type="text"
            id="descriptionHebrew"
            value={this.state.editBusiness.description.hebrew}
            onChange={(e) => this.setState({
              editBusiness: {
                description: {
                  hebrew: e.currentTarget.value
                }
              }
            })}
            placeholder="description Hebrew"
          />
          <input
            type="text"
            id="descriptionEnglish"
            value={this.state.editBusiness.description.english}
            onChange={(e) => this.setState({
              editBusiness: {
                description: {
                  english: e.currentTarget.value
                }
              }
            })}
            placeholder="description English"
          />
          <input
            type="checkbox"
            checked={this.state.editBusiness.visible}
            onChange={(e) => this.setState({
              editBusiness: {
                visible: e.target.checked
              }
            })}
          />
        </form>

      )
    } else {
      return(
        <div>
          Choose business to edit
        </div>
      )
    }
  }

    render() {
      debugger
        return (
          <div>
            <div>
              <span>Edit Businesses</span>
            </div>
            <div>
              <ul>
                {this.titlesArray()}
              </ul>
            </div>
            <div>
              {this.createEditForm()}
            </div>
          </div>
        );
    }
}

export default withRouter(Business);
