import { RECEIVE_ALL_BUSINESSES } from "../../actions/business_actions"

export default function (state = {}, action) {
  switch (action.type) {
    case RECEIVE_ALL_BUSINESSES:
      debugger
      let businessesArray = action.businesses.data.map(business => {
        const { description, displayName, visible, _id } = business;
        return {
          _id: {
            description: {
              id: description._id,
              hebrew: description.hebrew,
              english: description.english,
            },
            displayName: {
              id: displayName._id,
              hebrew: displayName.hebrew,
              english: displayName.english,
            },
            visible
          },
        };
      })
      return Object.assign(state, {}, )
    default:
      return state;
  }
}
