import { connect } from "react-redux";
import { fetchBusinesses, createBusiness, editBusiness } from "../../../actions/business_actions";
import Business from "./business";

const mapStateToProps = (state) => {
  debugger
  return {
      businesses: state.entities.businesses
  }
};

const mapDispatchToProps = (dispatch) => {
  return {
    fetchAll: () => dispatch(fetchBusinesses()),
    createNew: (business) => dispatch(createBusiness(business)),
    edit: (business) => dispatch(editBusiness(business))
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Business);
