import React from "react";
import { withRouter } from "react-router-dom";
import BusinessForm from "./business_form";

class Business extends React.Component {
    constructor(props) {
        super(props)

      this.state = { 
        businesses: {},
        currentBusiness: {}
      };

      this.chooseEditForm = this.chooseEditForm.bind(this);
      this.titlesArray = this.titlesArray.bind(this);
      this.chooseNewForm = this.chooseNewForm.bind(this);

      this.formType = 'New Business'
    }

    componentDidMount() {
      this.props.fetchAll()
    }

    componentDidUpdate() {
      debugger
      if (this.props.businesses !== this.state.businesses) {
        this.setState({ businesses: this.props.businesses });
      }
    }

    chooseEditForm(e) {
      e.preventDefault();
      debugger
      let id = e.target.id
      let business = this.state.businesses[id]
      let newBusiness = {
        id,
        displayNameHebrew: business.displayName.hebrew,
        displayNameEnglish: business.displayName.english,
        descriptionHebrew: business.description.hebrew,
        descriptionEnglish: business.description.english,
        visible: business.visible
      }
      this.setState({ currentBusiness: newBusiness})
      this.formType = 'Edit Business'
    }

    titlesArray() {
      const { businesses } = this.state;
      let ids = Object.keys(businesses);
      let array = [];
      if (ids.length > 0) {
        for (let i = 0; i < ids.length; i++) {
          const id = ids[i];
          const business = businesses[id];
          array.push(
            <li key={id} id={id} onClick={this.chooseEditForm}>
              Edit {business.displayName.hebrew}
            </li>
          )

        }
      }
      return array;
    }

    chooseNewForm(e) {
      e.preventDefault();
      debugger
      let newBusiness = {
        id: '',
        displayNameHebrew: '',
        displayNameEnglish: '',
        descriptionHebrew: '',
        descriptionEnglish: '',
        visible: ''
      }
      this.setState({ currentBusiness: newBusiness })
      this.formType = 'New Business'
    }

    render() {
      debugger
        return (
          <div>
            <div>
              <span>Edit Businesses</span>
            </div>
            <div>
              <ul>
                {this.titlesArray()}
              <li onClick={this.chooseNewForm}>
                Create New Business
              </li>
              </ul>
            </div>
            <div>
              <BusinessForm business={this.state.currentBusiness} formType={this.formType}/>
            </div>
          </div>
        );
    }
}

export default withRouter(Business);
