import React, { useState } from "react";

const Business = (props) => {
    debugger

    [newBusiness, setNewBusiness] = useState(() => ({
      id: "",
      displayNameHebrew: "",
      displayNameEnglish: "",
      descriptionHebrew: "",
      descriptionEnglish: "",
      visible: true,
    }));

    function update(field) {
        debugger
        return (e) => {
            if (e.target.type === "checkbox") {
                setNewBusiness((prev) => ({ ...prev, [field]: !!e.target.checked }))
            } else {
                setNewBusiness((prev) => ({ ...prev, [field]: !!e.target.value }))
            }
        }
    }
}

export default Business;
