import React from "react";
import { withRouter } from "react-router-dom";
import BusinessForm from "./business_form";

class Business extends React.Component {
    constructor(props) {
        super(props)

      this.state = { 
        businesses: {},
        currentBusiness: {}
      };

      this.chooseEdit = this.chooseEdit.bind(this);
      this.titlesArray = this.titlesArray.bind(this);
    }

    componentDidMount() {
      this.props.fetchAll()
    }

    componentDidUpdate() {
      debugger
      if (this.props.businesses !== this.state.businesses) {
        this.setState({ businesses: this.props.businesses });
      }
    }

    chooseEdit(e) {
      e.preventDefault();
      debugger
      let id = [e.target.id]
      let business = this.state.businesses[id]
      let newBusiness = {
        id,
        displayNameHebrew: business.displayName.hebrew,
        displayNameEnglish: business.displayName.english,
        descriptionHebrew: business.description.hebrew,
        descriptionEnglish: business.description.english,
        visible: business.visible
      }
      this.setState({ editBusiness: newBusiness})
    }

    titlesArray() {
      const { businesses } = this.state;
      let ids = Object.keys(businesses);
      let array = [];
      if (ids.length > 0) {
        for (let i = 0; i < ids.length; i++) {
          const id = ids[i];
          const business = businesses[id];
          array.push(
            <li key={id} id={id} onClick={this.chooseEdit}>
              Edit {business.displayName.hebrew}
            </li>
          )

        }
      }
      return array;
    }

    render() {
      debugger
        return (
          <div>
            <div>
              <span>Edit Businesses</span>
            </div>
            <div>
              <ul>
                {this.titlesArray()}
              </ul>
            </div>
            <div>
              <BusinessForm business={this.state.currentBusiness}/>
            </div>
          </div>
        );
    }
}

export default withRouter(Business);
