import * as APIUtil from "../util/business_api_util";

export const RECEIVE_ALL_BUSINESSES = "RECEIVE_ALL_BUSINESSES";
export const RECEIVE_BUSINESS = "RECEIVE_BUSINESS";
export const RECEIVE_BUSINESSES_ERRORS = "RECEIVE_BUSINESSES_ERRORS";
export const REMOVE_BUSINESS = "REMOVE_BUSINESS";

export const receiveAllBusinesses = (businesses) => ({
  type: RECEIVE_ALL_BUSINESSES,
  businesses,
});

export const receiveBusiness = (business) => ({
  type: RECEIVE_BUSINESS,
  business
})

export const receiveErrors = (errors) => ({
  type: RECEIVE_BUSINESSES_ERRORS,
  errors
})

export const removeBusiness = (businessId) => ({
  type: REMOVE_BUSINESS,
  businessId
})

export const fetchBusinesses = () => (dispatch) => {
  APIUtil.getAllBusinesses()
    .then((businesses) => {
      dispatch(receiveAllBusinesses(businesses));
    })
    .catch((err) => {
      debugger
      dispatch(receiveErrors(err));
    });
};

export const createBusiness = (business) => (dispatch) => {
  APIUtil.createBusiness(business)
    .then((business) => {
      dispatch(receiveBusiness(business));
    })
    .catch((err) => {
      debugger
      dispatch(receiveErrors(err));
    });
};

export const editBusiness = (business) => (dispatch) => {
  APIUtil.editBusiness(business)
    .then((business) => {
      dispatch(receiveBusiness(business));
    })
    .catch((err) => {
      debugger
      dispatch(receiveErrors(err));
    });
};

export const deleteBusiness = (businessId) => (dispatch) => {
  APIUtil.deleteBusiness(businessId)
    .then(() => {
      dispatch(removeBusiness(businessId));
    })
    .catch((err) => {
      debugger
      dispatch(receiveErrors(err));
    });
};
