import React from "react";
import { withRouter } from "react-router-dom";

class Business extends React.Component {
    constructor(props) {
        super(props)

      this.state = { 
        businesses: {} ,
        editBusiness: {},
        newBusiness: {}
      };

      this.chooseEdit = this.chooseEdit.bind(this);
      this.titlesArray = this.titlesArray.bind(this);
      this.update = this.update.bind(this);
    }

    componentDidMount() {
      this.props.fetchAll()
    }

    componentDidUpdate() {
      debugger
      if (this.props.businesses !== this.state.businesses) {
        this.setState({ businesses: this.props.businesses });
      }
    }

    chooseEdit(e) {
      e.preventDefault();
      debugger
      this.setState({ editBusiness: {
        [e.target.id]: this.state.businesses[e.target.id]
      }})
    }

    titlesArray() {
      const { businesses } = this.state;
      let ids = Object.keys(businesses);
      let array = [];
      if (ids.length > 0) {
        for (let i = 0; i < ids.length; i++) {
          const id = ids[i];
          const business = businesses[id];
          array.push(
            <li key={id} id={id} onClick={this.chooseEdit}>
              Edit {business.displayName.hebrew}
            </li>
          )

        }
      }
      return array;
    }

  update(field) {
    return e =>
      this.setState({
        [field]: e.currentTarget.value
      });
  }

    render() {
      debugger
        return (
          <div>
            <div>
              <span>Edit Businesses</span>
            </div>
            <div>
              <ul>
                {this.titlesArray()}
              </ul>
            </div>
            <div>
              <form>
                <input
                  type="text"
                  id="displayNameHebrew"
                  value={this.state.editBusiness.displayName.hebrew}
                  onChange={this.update("workAddress")}
                  placeholder="Work Address"
                />
              </form>
            </div>
          </div>
        );
    }
}

export default withRouter(Business);
