import { RECEIVE_ALL_BUSINESSES, RECEIVE_BUSINESSES_TITLES } from "../../actions/business_actions"

export default function (state = {}, action) {
  switch (action.type) {
    case RECEIVE_BUSINESSES_TITLES:
      debugger
      let businessesObject = {};
      action.businesses.data.forEach(business => {
        const { displayName, _id } = business;
        businessesObject[_id] = {
            displayName: displayName.hebrew,
          }
      })
      return Object.assign(state, {}, businessesObject)
    default:
      return state;
  }
}
