import { RECEIVE_ALL_BUSINESSES } from "../../actions/business_actions"

export default function (state = {}, action) {
  switch (action.type) {
    case RECEIVE_ALL_BUSINESSES:
      debugger
      let businessesArray = action.businesses.data.map(business => {
        const { description, displayName, visible } = business;
        return {
          [business._id]: {
            description: {
              id: businesses.description._id,
              hebrew: business.description.hebrew,
              english: business.description.english,
            },
            displayName: {
              id: businesses.displayName._id,
              hebrew: business.displayName.hebrew,
              english: business.displayName.english,
            },
            visible: businesses.visible
          },
        };
      })
      return Object.assign(state, {}, )
    default:
      return state;
  }
}
