import React from "react";
import { withRouter } from "react-router-dom";

class Business extends React.Component {
    constructor(props) {
        super(props)

      this.state = { businesses: this.props.businesses }
      this.handleClick = this.handleClick.bind(this)
      this.businessesArray = this.businessesArray.bind(this)
    }

    handleClick() {
        this.props.fetchAll()
        this.setState({businesses: this.props.businesses})
    }

    componentDidUpdate() {
      debugger
      if (this.props.businesses !== this.state.businesses) {
        this.setState({ businesses: this.props.businesses });
      }
    }

    businessesArray() {
      let { businesses } = this.props;

      if (businesses) {
        debugger
        let array = Object.keys(businesses).map(id => {
          let value = businesses[id];
          return (
            <li key={id} id={id}>
              {value.displayName.hebrew}
            </li>
          )
        })
        return array;
      } else {
        return [];
      }
    }

    render() {
      debugger
        return (
          <div>
            <div>
              <button onClick={this.handleClick}>Fetch All Businesses</button>
            </div>
            <div>
              <ul>
                {this.businessesArray()}
              </ul>
            </div>
          </div>
        );
    }
}

export default withRouter(Business);
