import React, { useState } from "react";
import { withRouter } from "react-router-dom";

class BusinessForm extends React.Component {
    constructor(props) {
        super(props)

        this.state = {
                id: '',
                displayNameHebrew: '',
                displayNameEnglish: '',
                descriptionHebrew: '',
                descriptionEnglish: '',
                visible: true
    };
        this.update = this.update.bind(this);
        this.sendForm = this.sendForm.bind(this);
}

    componentDidUpdate() {
        debugger
        if (this.props.business.id && this.props.business.id !== this.state.id) {
            this.setState(this.props.business);
        }
    }

    titlesArray() {
        const { businesses } = this.state;
        let ids = Object.keys(businesses);
        let array = [];
        if (ids.length > 0) {
            for (let i = 0; i < ids.length; i++) {
                const id = ids[i];
                const business = businesses[id];
                array.push(
                    <li key={id} id={id} onClick={this.chooseEdit}>
                        Edit {business.displayName.hebrew}
                    </li>
                )

            }
        }
        return array;
    }

    update(field) {
        debugger
        return (e) => {
            if (e.target.type === "checkbox") {
                this.setState({ [field]: !!e.target.checked })
            } else {
                this.setState({ [field]: e.target.value })
            }
        }
    }

    sendForm() {
        debugger
        const { displayNameHebrew, displayNameEnglish, descriptionHebrew, descriptionEnglish, visible, id } = this.state
        let business = {
            displayName: {
                hebrew: displayNameHebrew,
                english: displayNameEnglish
            },
            description: {
                hebrew: descriptionHebrew,
                english: descriptionEnglish
            },
            visible
        }
        debugger
        if (this.props.formType === 'New Business') {
            this.props.create(business)
        } else {
            business.id = id;
            this.props.edit(business);
        }
        debugger
    }

    render() {
        debugger
            return (
                <form>
                    <h2>{this.props.formType}</h2>
                    <label>displayName hebrew
                        <input
                            type="text"
                            id="displayNameHebrew"
                            value={this.state.displayNameHebrew}
                            onChange={this.update('displayNameHebrew')}
                            placeholder="displayName Hebrew"
                        />
                    </label>
                    <label>displayName english
                        <input
                            type="text"
                            id="displayNameEnglish"
                            value={this.state.displayNameEnglish}
                            onChange={this.update('displayNameEnglish')}
                            placeholder="displayName Hebrew"
                        />
                    </label>
                    <label>Description hewbrew
                        <input
                            type="text"
                            id="descriptionHebrew"
                            value={this.state.descriptionHebrew}
                            onChange={this.update('descriptionHebrew')}
                            placeholder="displayName English"
                        />
                    </label>
                    <label>
                        Description english
                        <input
                            type="text"
                            id="descriptionEnglish"
                            value={this.state.descriptionEnglish}
                            onChange={this.update('descriptionEnglish')}
                            placeholder="description Hebrew"
                        />
                    </label>
                    <label>
                        Visible
                        <input
                            type="checkbox"
                            checked={this.state.visible}
                            onChange={this.update('visible')}
                        />
                    </label>
                    <button onClick={this.sendForm}>{this.props.formType === 'New Business' ? "create the form" : "edit the form"}</button>
                </form>

            )
    }
}

export default withRouter(BusinessForm);
