import React from "react";
import { withRouter } from "react-router-dom";

class VusinessForm extends React.Component {
    constructor(props) {
        super(props)

        this.state = {
            businesses: {
                id: '',
                displayNameHebrew: '',
                displayNameEnglish: '',
                descriptionHebrew: '',
                descriptionEnglish: '',
                visible: ''
            }
        };

        this.update = this.update.bind(this);
    }

    componentDidMount() {
        this.props.fetchAll()
    }

    componentDidUpdate() {
        debugger
        if (this.props.businesses !== this.state.businesses) {
            this.setState({ businesses: this.props.businesses });
        }
    }

    chooseEdit(e) {
        e.preventDefault();
        debugger
        let id = [e.target.id]
        let business = this.state.businesses[id]
        let newBusiness = {
            id,
            displayNameHebrew: business.displayName.hebrew,
            displayNameEnglish: business.displayName.english,
            descriptionHebrew: business.description.hebrew,
            descriptionEnglish: business.description.english,
            visible: business.visible
        }
        this.setState({ editBusiness: newBusiness })
    }

    titlesArray() {
        const { businesses } = this.state;
        let ids = Object.keys(businesses);
        let array = [];
        if (ids.length > 0) {
            for (let i = 0; i < ids.length; i++) {
                const id = ids[i];
                const business = businesses[id];
                array.push(
                    <li key={id} id={id} onClick={this.chooseEdit}>
                        Edit {business.displayName.hebrew}
                    </li>
                )

            }
        }
        return array;
    }

    update(field) {
        return (e) => {
            let newState
            if (e.target.type === "checkbox") {
                newState = Object.assign({}, this.state.editBusiness, { [field]: e.target.checked })
                this.setState({ editBusiness: newState })
            } else {
                newState = Object.assign({}, this.state.editBusiness, { [field]: e.target.value })
                this.setState({ editBusiness: newState })
            }
        }
    }

    render() {
        if (this.state.editBusiness.id) {
            return (
                <form>
                    <label>displayName hebrew
          <input
                            type="text"
                            id="displayNameHebrew"
                            value={this.state.editBusiness.displayNameHebrew}
                            onChange={this.update('displayNameHebrew')}
                            placeholder="displayName Hebrew"
                        />
                    </label>
                    <label>displayName english
          <input
                            type="text"
                            id="displayNameEnglish"
                            value={this.state.editBusiness.displayNameEnglish}
                            onChange={this.update('displayNameEnglish')}
                            placeholder="displayName Hebrew"
                        />
                    </label>
                    <label>Description hewbrew
          <input
                            type="text"
                            id="descriptionHebrew"
                            value={this.state.editBusiness.descriptionHebrew}
                            onChange={this.update('descriptionHebrew')}
                            placeholder="displayName English"
                        />
                    </label>
                    <label>
                        Description english
          <input
                            type="text"
                            id="descriptionEnglish"
                            value={this.state.editBusiness.descriptionEnglish}
                            onChange={this.update('descriptionEnglish')}
                            placeholder="description Hebrew"
                        />
                    </label>
                    <label>
                        Visible
          <input
                            type="checkbox"
                            checked={this.state.editBusiness.visible}
                            onChange={this.update('visible')}
                        />
                    </label>
                </form>

            )
        } else {
            return (
                <div>
                    Choose business to edit
                </div>
            )
        }
    }
}

export default withRouter(VusinessForm);
